mapper.select:
SELECT

from t_coupon_quota_detail

WHERE valid=1


  AND sys_brand_id=#{vo.sysBrandId}  




 AND (staff_name LIKE '%${vo.baseSearchValue}%'
     OR store_name LIKE '%${vo.baseSearchValue}%')



  AND staff_code=#{vo.staffCode}  


    AND coupon_name LIKE '%${vo.couponName}%'


  AND coupon_quota_id=#{vo.couponQuotaId}  


  AND coupon_quota_detail_id=#{vo.couponQuotaDetailId}  


   AND start_date_start <= #{vo.extendNote} AND start_date_end >= #{vo.extendNote}


ORDER BY create_date DESC

SELECT

from t_coupon_quota_detail

WHERE 1=1


  AND sys_brand_id=#{vo.sysBrandId}  




 AND (staff_name LIKE '%${vo.baseSearchValue}%'
     OR store_name LIKE '%${vo.baseSearchValue}%')



  AND staff_code=#{vo.staffCode}  


    AND coupon_name LIKE '%${vo.couponName}%'


  AND coupon_quota_id=#{vo.couponQuotaId}  


  AND coupon_quota_detail_id=#{vo.couponQuotaDetailId}  


   AND start_date_start <= #{vo.extendNote} AND start_date_end >= #{vo.extendNote}


ORDER BY create_date DESC
