log.msg_sms_log_id,
log.send_state,
log.member_name,
log.member_phone,
log.create_date,
log.template_type
FROM
t_msg_sms_log log



  AND log.member_phone=#{memberPhone}


  AND log.template_type=#{templateType}





    AND log.create_date >= #{sendTimeStart} AND log.create_date <= #{sendTimeEnd}






      AND log.create_date >= #{sendTimeStart}







      AND  log.create_date <= #{sendTimeEnd}






  AND log.member_phone LIKE "%"#{baseSearchValue}"%" OR log.template_type LIKE "%"#{baseSearchValue}"%"

