mapper.select:
SELECT log.wechat_message_log_id, log.send_state, log.open_id, log.member_name, log.member_phone, log.wx_nick, log.template_type, log.create_date FROM t_msg_wx_log log WHERE 1=1
AND log.member_name LIKE "%"#{vo.memberName}"%"
AND log.member_phone=#{vo.memberPhone}
AND log.wx_nick LIKE "%"#{vo.wxNick}"%"
AND log.open_id=#{vo.openId}
AND log.template_type=#{vo.templateType}
AND date_format(log.create_date,'%Y-%m-%d') >= #{vo.sendTimeStart} AND date_format(log.create_date,'%Y-%m-%d') <= #{vo.sendTimeEnd}
AND log.member_name LIKE "%"#{vo.searchValue}"%" OR log.template_type LIKE "%"#{vo.searchValue}"%"
