select tma.mkt_activity_id, tma.sys_company_id, tma.sys_brand_id, tma.activity_code, tma.activity_type, tma.activity_name, tma.start_time, tma.end_time, tma.long_term, tma.points, tma.check_status, tma.activity_status, tma.remark, tma.create_user_id, tma.create_user_name, tma.create_date, tma.modified_user_id, tma.modified_user_name, tma.modified_date, tma.valid,
tmm.receive_type, tmm.per_person_per_day_max, tmm.per_person_max, tmm.remark, tmm.create_user_name, tmm.create_date, tmm.modified_user_name, tmm.modified_date, tmm.valid from t_mkt_activity tma left join t_mkt_activity_manual tmm on tma.mkt_activity_id = tmm.mkt_activity_id where tmm.valid=1 and tma.valid=1
AND tmm.receive_type = #{receiveType,jdbcType=INTEGER}
AND tma.activity_status = #{activityStatus,jdbcType=INTEGER}
AND tma.create_date >= #{createDateStart,jdbcType=TIMESTAMP}
AND tma.create_date < = #{createDateEnd,jdbcType=TIMESTAMP}
and tma.start_time >=#{startTime,jdbcType=TIMESTAMP}
and tma.end_time <=#{endTime,jdbcType=TIMESTAMP}

SELECT tma.mkt_activity_id, tma.sys_company_id, tma.sys_brand_id, tma.activity_code, tma.activity_type, tma.activity_name, tma.start_time, tma.end_time, tma.long_term, tma.points, tma.check_status, tma.activity_status, tma.remark, tma.create_user_id, tma.create_user_name, tma.create_date, tma.modified_user_id, tma.modified_user_name, tma.modified_date, tma.valid,
tmm.receive_type, tmm.per_person_per_day_max, tmm.per_person_max, tmm.remark, tmm.create_user_name, tmm.create_date, tmm.modified_user_name, tmm.modified_date, tmm.valid FROM `t_mkt_activity` tma INNER JOIN t_mkt_activity_manual tmm ON tma.mkt_activity_id = tmm.mkt_activity_id and tma.valid=1 and tmm.valid=1
AND tma.mkt_activity_id = #{mktActivityId,jdbcType=BIGINT}

SELECT tma.mkt_activity_id, tma.activity_code, tma.activity_name, tma.start_time, tma.end_time, tma.activity_info, tma.long_term, tma.activity_list_img, tma.activity_detail_img, tma.check_status, tma.activity_status, tma.remark, tma.create_user_id, tma.create_user_name, tma.create_date, tmam.receive_type, tmam.qrcode, tmam.per_person_per_day_max, tmam.per_person_max, tmam.remark, tmam.mkt_activity_manual_id, tmc.coupon_name, tmc.coupon_code
FROM t_mkt_activity tma LEFT JOIN t_mkt_activity_manual tmam ON tma.mkt_activity_id = tmam.mkt_activity_id left join t_mkt_coupon tmc on tmc.biz_id = tma.mkt_activity_id WHERE tmam.valid = 1 AND tma.valid = 1 and tmc.valid=1
AND tma.sys_brand_id = #{sysBrandId,jdbcType=BIGINT}
AND tma.sys_company_id = #{sysCompanyId,jdbcType=BIGINT}
AND tma.activity_code = #{activityCode,jdbcType=VARCHAR}
AND tma.activity_type = #{activityType,jdbcType=INTEGER}

SELECT tma.activity_name, tma.start_time, tma.end_time, tmam.receive_type FROM t_mkt_activity tma LEFT JOIN t_mkt_activity_manual tmam ON tma.mkt_activity_id = tmam.mkt_activity_id WHERE tma.valid=1 and tmam.valid=1
AND tma.sys_company_id = #{sysCompanyId}
AND tma.sys_brand_id = #{sysBrandId}
and receive_type = #{receiveType,jdbcType=INTEGER},
