mapper.update:
update t_mkt_activity
set
activity_status = #{activityStatus}
where activity_code = #{activityCode}
AND check_status = 3  AND valid = 1

mapper.select:
select t.activity_detail_img,t.activity_type activityType,t.activity_code as activityCode,t.activity_name as activityName,t.mkt_activity_id as mktActivityId ,s.is_store_limit,s.store_limit_type,s.store_limit_list from t_mkt_activity t INNER JOIN t_mkt_activity_order s on t.mkt_activity_id=s.mkt_activity_id where t.valid=1 and t.activity_status=2
AND t.sys_brand_id = #{sysBrandId}
UNION ALL select t.activity_detail_img,t.activity_type activityType,t.activity_code as activityCode,t.activity_name as activityName,t.mkt_activity_id as mktActivityId ,f.is_store_limit,f.store_limit_type,f.store_limit_list from t_mkt_activity t INNER JOIN t_mkt_activity_upgrade f on t.mkt_activity_id=f.mkt_activity_id where t.valid=1 and t.activity_status=2
AND t.sys_brand_id = #{sysBrandId}
UNION ALL select t.activity_detail_img,t.activity_type activityType,t.activity_code as activityCode,t.activity_name as activityName,t.mkt_activity_id as mktActivityId ,t.is_store_limit,t.store_limit_type,t.store_limit_list from t_mkt_activity t where t.valid=1 and t.activity_status=2 and (t.activity_type=11 OR t.activity_type=12)
AND t.sys_brand_id = #{sysBrandId}

SELECT SUM(a.member_count)participateTotal, SUM(a.points_sum)pointsSumTotal FROM t_mkt_activity t JOIN t_mkt_activity_count a ON a.mkt_activity_id = t.mkt_activity_id WHERE t.valid=1
AND t.sys_brand_id = #{sysBrandId}
AND t.activity_type = #{activityType}
AND t.activity_type IN (3,4)

SELECT 4-count(tma.mkt_activity_id) FROM t_mkt_activity tma INNER JOIN t_mkt_message tmm ON tma.mkt_activity_id = tmm.biz_id WHERE tma.valid=1 AND tmm.msg_type=3
AND tma.mkt_activity_smart_group_id =#{mktActivitySmartGroupId}
AND tma.mbr_group_def_id=#{mbrGroupDefId}
AND tma.create_date LIKE CONCAT(#{time},'%')

select * FROM t_mkt_activity t where t.valid=1 and t.activity_status=2 and t.activity_type=11
AND CASE when t.is_store_limit = 1 then concat(',',t.store_limit_list,',') like concat('%,',#{serviceStoreId},',%') ELSE t.is_store_limit = 0 END
AND t.sys_brand_id = #{sysBrandId}

SELECT mkt_activity_id, sys_company_id, sys_brand_id, mbr_group_def_id, mkt_activity_smart_group_id, activity_code, activity_type, activity_name, start_time, end_time, activity_info, activity_rule, long_term, send_immediately, send_time, points, activity_list_img, activity_detail_img, qr_code_url, check_status, activity_status, is_store_limit, store_limit_type, store_limit_list, prize_points, remark, create_user_id, create_user_name, create_date, modified_user_id, modified_user_name, modified_date, valid FROM t_mkt_activity WHERE valid=1 AND activity_type=#{activityType}
AND sys_brand_id=#{brandId}
AND activity_status=#{activityStatus}
AND CONCAT(activity_name,activity_code) LIKE concat('%',#{activityName},'%')
ORDER BY create_date DESC

SELECT tmc.mkt_activity_id, tmc.sys_company_id, tmc.sys_brand_id, tmc.activity_code, tmc.activity_type, tmc.activity_name, tmc.start_time, tmc.end_time, tmc.activity_info, tmc.activity_rule, tmc.long_term, tmc.activity_list_img, tmc.activity_detail_img, tmc.qr_code_url, tmc.check_status, tmc.activity_status, tmc.is_store_limit, tmc.store_limit_type, tmc.store_limit_list, tmarp.mkt_activity_red_packet_id, tmarp.coupon_definition_id, tmarp.coupon_name, tmarp.coupon_denomination, tmarp.add_coupon_denomination, tmarp.do_if_reward, tmarp.reward_integral FROM t_mkt_activity tmc INNER JOIN t_mkt_activity_red_packet tmarp ON tmc.mkt_activity_id=tmarp.mkt_activity_id AND tmc.valid=1 AND tmarp.valid=1
tmc.mkt_activity_id=#{mktActivityId}
tmc.activity_code=#{activityCode}
