mapper.update:
update t_mkt_activity
set
activity_status = #{activityStatus}
where activity_code = #{activityCode}
AND check_status = 3  AND valid = 1

mapper.select:
select t.activity_type activityType,t.activity_code as activityCode,t.activity_name as activityName,t.mkt_activity_id as mktActivityId ,s.is_store_limit,s.store_limit_type,s.store_limit_list from t_mkt_activity t INNER JOIN t_mkt_activity_order s on t.mkt_activity_id=s.mkt_activity_id where t.valid=1 and t.activity_status=2
AND t.sys_brand_id = #{sysBrandId}
UNION ALL select t.activity_type activityType,t.activity_code as activityCode,t.activity_name as activityName,t.mkt_activity_id as mktActivityId ,f.is_store_limit,f.store_limit_type,f.store_limit_list from t_mkt_activity t INNER JOIN t_mkt_activity_upgrade f on t.mkt_activity_id=f.mkt_activity_id where t.valid=1 and t.activity_status=2
AND t.sys_brand_id = #{sysBrandId}

SELECT SUM(a.member_count)participateTotal, SUM(a.points_sum)pointsSumTotal FROM t_mkt_activity t JOIN t_mkt_activity_count a ON a.mkt_activity_id = t.mkt_activity_id WHERE t.valid=1
AND t.sys_brand_id = #{sysBrandId}
AND t.activity_type = #{activityType}
AND t.activity_type IN (3,4)

SELECT 4-count(tma.mkt_activity_id) FROM t_mkt_activity tma INNER JOIN t_mkt_message tmm ON tma.mkt_activity_id = tmm.biz_id WHERE tma.valid=1 AND tmm.msg_type=3
AND tma.mkt_activity_smart_group_id =#{mktActivitySmartGroupId}
AND tma.mbr_group_def_id=#{mbrGroupDefId}
AND tma.create_date LIKE CONCAT(#{time},'%')
