mapper.select:
select mkt_activity_prize_record_id, mkt_activity_id, sys_company_id, sys_brand_id, member_code, member_phone, member_name, prize_type, awad_type, prize_name, prize_time, coupon_definition_id, coupon_definition_code, is_win_prize, remark, create_user_id, create_user_name, create_date, modified_user_id, modified_user_name, modified_date, valid FROM t_mkt_activity_prize_record WHERE valid=1 AND awad_type!=#{prizeType}
AND mkt_activity_id=#{mktActivityId}
AND CONCAT(member_phone,member_name,prize_name) LIKE concat('%',#{activityName},'%')
ORDER BY prize_time DESC

    SELECT
	count(mkt_activity_prize_record_id)
    FROM
        t_mkt_activity_prize_record
    WHERE
    
	mkt_activity_id = #{mktActivityId}
    
    
	AND prize_type = #{prizeType}
    
    
	AND member_code=#{memberCode}
    
	AND valid=1
