mapper.select:
    SELECT
    count(mkt_activity_red_packet_record_id)
    FROM
	t_mkt_activity_red_packet_record
    WHERE
	valid = 1

      AND type=#{type}


	AND member_code = #{memberCode}


    AND sponsor_code = #{sponsorCode}


	AND mkt_activity_id=#{mktActivityId}

SELECT mkt_activity_red_packet_record_id, mkt_activity_id, sys_company_id, sys_brand_id, member_code, member_phone, member_name, sponsor_code, coupon_definition_id, coupon_name, coupon_code, get_coupon_date, reward_integral, type, coupon_quota, wx_head_url FROM t_mkt_activity_red_packet_record WHERE valid = 1 AND type=3
AND sys_brand_id=#{sysBrandId}
AND member_code = #{memberCode}
AND mkt_activity_id=#{mktActivityId}
AND CONCAT(member_name,member_phone,coupon_name,coupon_code) LIKE concat('%',#{activityName},'%')
ORDER BY create_date DESC

SELECT mkt_activity_red_packet_record_id, mkt_activity_id, sys_company_id, sys_brand_id, member_code, member_phone, member_name, sponsor_code, coupon_definition_id, coupon_name, get_coupon_date, reward_integral, add_coupon_denomination, type FROM t_mkt_activity_red_packet_record WHERE valid = 1 AND type=2
AND mkt_activity_id=#{mktActivityId}
AND sponsor_code=#{sponsorCode}
