mapper.select:
SELECT DATE_FORMAT(statistics_time, '%Y-%m-%d') FROM t_mkt_activity_statistics
WHERE mkt_activity_id = #{activityId}
GROUP BY statistics_time

select

  from t_mkt_activity_statistics


      mkt_activity_id = #{activityId}


      and DATE_FORMAT(statistics_time,'%Y-%m-%d') = #{statisticsTime}


      and statistics_type = #{statisticsType}


        and sys_company_id = #{sysCompanyId}


        and sys_brand_id = #{sysBrandId}
