SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, tto.mkt_task_order_id, tto.order_source, tto.consume_amount, tto.consume_times, tto.is_store_limit, tto.store_limit_type, tto.store_limit_list, tmc.mkt_coupon_id, tmc.coupon_definition_id, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_order tto ON tta.mkt_task_id = tto.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id AND tmc.valid = 1 WHERE tta.valid = 1 AND tto.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type BETWEEN 4 AND 5
AND tta.sys_company_id=#{sysCompanyId}
AND tta.sys_brand_id=#{sysBrandId}
AND tta.start_time <=#{placeOrderTime} AND tta.end_time >= #{placeOrderTime}
AND (tto.order_source=#{orderSource} or tto.order_source=0)

SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, tti.mkt_task_id, tti.invite_num, tti.is_store_limit, tti.store_limit_type, tti.store_limit_list, tmc.mkt_coupon_id, tmc.coupon_definition_id, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_invite tti ON tta.mkt_task_id = tti.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id AND tmc.valid = 1 WHERE tta.valid = 1 AND tti.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type =3
AND tta.mkt_task_id=#{mktTaskId}
AND tta.sys_company_id=#{sysCompanyId}
AND tta.sys_brand_id=#{sysBrandId}
AND tta.start_time <=#{placeOrderTime} AND tta.end_time >= #{placeOrderTime}

SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, ttp.mkt_task_profile_id, ttp.property_code, ttp.is_store_limit, ttp.store_limit_type, ttp.store_limit_list, tmc.mkt_coupon_id, tmc.coupon_definition_id, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_profile ttp ON tta.mkt_task_id = ttp.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id AND tmc.valid = 1 WHERE tta.valid = 1 AND ttp.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type =1
AND tta.mkt_task_id=#{mktTaskId}
AND tta.sys_company_id=#{sysCompanyId}
AND tta.sys_brand_id=#{sysBrandId}

SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, share_url, tts.mkt_task_share_id, tts.share_times, tts.share_url_type, tts.is_store_limit, tts.store_limit_type, tts.store_limit_list, tmc.mkt_coupon_id, tmc.coupon_definition_id, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_share tts ON tta.mkt_task_id = tts.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id AND tmc.valid = 1 WHERE tta.valid = 1 AND tts.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type =2
AND tta.mkt_task_id=#{mktTaskId}
AND tta.sys_company_id=#{sysCompanyId}
AND tta.sys_brand_id=#{sysBrandId}
AND tta.start_time <=#{shareDate} AND tta.end_time >= #{shareDate}

SELECT tmt.mkt_task_id AS taskId, tmt.task_code, tmt.task_name, tmt.task_type, tmt.start_time, tmt.end_time, IF(tmtr.rewarded=1,"1/1", "0/1") AS task_progress , IF(tmtr.rewarded IS NULL,0,1) AS rewarded, 2 as sourceType FROM t_mkt_task tmt INNER JOIN t_mkt_task_profile tmto ON tmt.mkt_task_id=tmto.mkt_task_id LEFT JOIN t_mkt_task_record tmtr ON tmtr.task_id=tmto.mkt_task_id AND tmtr.valid = 1 AND tmtr.member_code=#{memberCode} WHERE tmt.valid=1 AND tmt.task_type =1 AND tmto.valid=1 AND tmt.check_status=3 AND tmt.task_status=2 AND (tmto.is_store_limit = 0 OR tmto.store_limit_list LIKE #{storeIdStyleOne} OR tmto.store_limit_list LIKE #{storeIdStyleTwo} OR tmto.store_limit_list LIKE #{storeIdStyleThree} OR tmto.store_limit_list =#{storeId}) AND tmt.sys_brand_id=#{brandId} GROUP BY tmt.mkt_task_id,tmt.task_type,tmt.sys_brand_id,tmtr.member_code
HAVING rewarded = #{rewarded}
UNION
SELECT tmt.mkt_task_id AS taskId, tmt.task_code, tmt.task_name, tmt.task_type, tmt.start_time, tmt.end_time, CONCAT(count(tmtr.member_code),'/',tmto.share_times) AS task_progress, IF(count(tmtr.member_code)>=tmto.share_times,1,0) AS rewarded, 2 as sourceType FROM t_mkt_task tmt INNER JOIN t_mkt_task_share tmto ON tmt.mkt_task_id=tmto.mkt_task_id LEFT JOIN t_mkt_task_record tmtr ON tmtr.task_id=tmto.mkt_task_id AND tmtr.valid = 1 AND tmtr.member_code=#{memberCode} WHERE tmt.valid=1 AND tmto.valid=1 AND tmt.task_type =2 AND tmt.check_status=3 AND tmt.task_status=2 AND (tmto.is_store_limit = 0 OR tmto.store_limit_list LIKE #{storeIdStyleOne} OR tmto.store_limit_list LIKE #{storeIdStyleTwo} OR tmto.store_limit_list LIKE #{storeIdStyleThree} OR tmto.store_limit_list =#{storeId}) AND tmt.sys_brand_id=#{brandId} GROUP BY tmt.mkt_task_id,tmt.task_type,tmt.sys_brand_id,tmtr.member_code
HAVING rewarded = #{rewarded}
UNION
SELECT tmt.mkt_task_id AS taskId, tmt.task_code, tmt.task_name, tmt.task_type, tmt.start_time, tmt.end_time, CONCAT(count(tmtr.member_code),'/',tmto.invite_num) AS task_progress , IF(count(tmtr.member_code)>=tmto.invite_num,1,0) AS rewarded, 2 as sourceType FROM t_mkt_task tmt INNER JOIN t_mkt_task_invite tmto ON tmt.mkt_task_id= tmto.mkt_task_id LEFT JOIN t_mkt_task_record tmtr ON tmto.mkt_task_id=tmtr.task_id AND tmtr.valid = 1 AND tmtr.member_code=#{memberCode} WHERE tmt.valid=1 AND tmto.valid=1 AND tmt.task_type =3 AND tmt.check_status=3 AND tmt.task_status=2 AND (tmto.is_store_limit = 0 OR tmto.store_limit_list LIKE #{storeIdStyleOne} OR tmto.store_limit_list LIKE #{storeIdStyleTwo} OR tmto.store_limit_list LIKE #{storeIdStyleThree} OR tmto.store_limit_list =#{storeId}) AND tmt.sys_brand_id=#{brandId} GROUP BY tmt.mkt_task_id,tmt.task_type,tmt.sys_brand_id,tmtr.member_code
HAVING rewarded = #{rewarded}
UNION
SELECT tmt.mkt_task_id AS taskId, tmt.task_code, tmt.task_name, tmt.task_type, tmt.start_time, tmt.end_time, CONCAT(count(tmtr.member_code),'/',tmto.consume_times) AS task_progress , IF(count(tmtr.member_code)>=tmto.consume_times,1,0) AS rewarded, IF(tmto.order_source <> 1, 2,1) as sourceType FROM t_mkt_task tmt INNER JOIN t_mkt_task_order tmto ON tmt.mkt_task_id= tmto.mkt_task_id LEFT JOIN t_mkt_task_record tmtr ON tmto.mkt_task_id=tmtr.task_id AND tmtr.valid = 1 AND tmtr.member_code=#{memberCode} WHERE tmt.valid=1 AND tmt.task_type =4 AND tmto.valid=1 AND tmt.check_status=3 AND tmt.task_status=2 AND (tmto.is_store_limit = 0 OR tmto.store_limit_list LIKE #{storeIdStyleOne} OR tmto.store_limit_list LIKE #{storeIdStyleTwo} OR tmto.store_limit_list LIKE #{storeIdStyleThree} OR tmto.store_limit_list =#{storeId}) AND tmt.sys_brand_id=#{brandId} GROUP BY tmt.mkt_task_id,tmt.task_type,tmt.sys_brand_id,tmtr.member_code
HAVING rewarded = #{rewarded}
UNION
SELECT tmt.mkt_task_id AS taskId, tmt.task_code, tmt.task_name, tmt.task_type, tmt.start_time, tmt.end_time, IF(sum(tmtr.consume_amount)>=tmto.consume_amount,'1/1','0/1') AS task_progress, IF(sum(tmtr.consume_amount)>=tmto.consume_amount,1,0) AS rewarded, IF(tmto.order_source <> 1, 2,1) as sourceType FROM t_mkt_task tmt INNER JOIN t_mkt_task_order tmto ON tmt.mkt_task_id = tmto.mkt_task_id LEFT JOIN t_mkt_task_record tmtr ON tmto.mkt_task_id = tmtr.task_id AND tmtr.valid = 1 AND tmtr.member_code=#{memberCode} WHERE tmt.valid=1 AND tmt.task_type =5 AND tmto.valid=1 AND tmt.check_status=3 AND tmt.task_status=2 AND (tmto.is_store_limit = 0 OR tmto.store_limit_list LIKE #{storeIdStyleOne} OR tmto.store_limit_list LIKE #{storeIdStyleTwo} OR tmto.store_limit_list LIKE #{storeIdStyleThree} OR tmto.store_limit_list =#{storeId}) AND tmt.sys_brand_id=#{brandId} GROUP BY tmt.mkt_task_id,tmt.task_type,tmt.sys_brand_id,tmtr.member_code
HAVING rewarded = #{rewarded}
