mapper.select:
SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, tto.mkt_task_order_id, tto.order_source, tto.consume_amount, tto.consume_times, tmc.mkt_coupon_id, tmc.coupon_code, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_order tto ON tta.mkt_task_id = tto.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id WHERE tta.valid = 1 AND tto.valid = 1 AND tmc.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type BETWEEN 4 AND 5
AND sys_company_id=#{sysCompanyId}
AND sys_brand_id=#{sysBrandId}
AND tta.start_time <=#{placeOrderTime} AND tta.end_time >= #{placeOrderTime}

SELECT tta.mkt_task_id, tta.sys_company_id, tta.sys_brand_id, tta.task_code, tta.task_type, tta.task_name, tta.start_time, tta.end_time, tta.task_info, tta.points, tta.task_list_img, tta.task_detail_img, tta.check_status, tta.task_status, tta.remark, tti.mkt_task_id, tti.invite_num, tmc.mkt_coupon_id, tmc.coupon_code, tmc.coupon_name FROM t_mkt_task tta INNER JOIN t_mkt_task_invite tti ON tta.mkt_task_id = tti.mkt_task_id LEFT JOIN t_mkt_coupon tmc ON tta.mkt_task_id = tmc.biz_id WHERE tta.valid = 1 AND tti.valid = 1 AND tmc.valid = 1 AND tta.check_status=3 AND tta.task_status=2 AND tta.task_type =3
AND sys_company_id=#{sysCompanyId}
AND sys_brand_id=#{sysBrandId}
AND tta.start_time <=#{placeOrderTime} AND tta.end_time >= #{placeOrderTime}
