mapper.select:
SELECT tmt.mkt_task_id, tmt.sys_company_id, tmt.sys_brand_id, tmt.task_code, tmt.task_type, tmt.task_name, tmt.start_time, tmt.end_time, tmt.task_info, tmt.points, tmt.task_list_img, tmt.task_detail_img, tmt.check_status, tmt.task_status, tmt.remark, tmt.create_user_id, tmt.create_user_name, tmt.create_date, tmt.modified_user_id, tmt.modified_user_name, tmt.modified_date, tmt.valid, tmtp.property_code FROM `t_mkt_task` tmt INNER JOIN t_mkt_task_profile tmtp ON tmt.mkt_task_id = tmtp.mkt_task_id AND tmt.valid = 1 AND tmtp.valid = 1
AND tmt.mkt_task_id = #{mktTaskId}
AND tmt.task_status = #{taskStatus}
AND tmt.sys_brand_id = #{sysBrandId}

SELECT SUM(tmtr.points) AS oneTaskPoints, count(distinct tmtr.member_code) AS dayCompleteCountMbr, tmtr.participate_date as partDate FROM t_mkt_task_record tmtr INNER JOIN t_mkt_task tmt ON tmtr.task_id=tmt.mkt_task_id WHERE tmtr.valid = 1 AND tmt.valid=1 AND tmtr.rewarded = 1 AND tmtr.sys_brand_id =#{brandId} AND tmtr.task_type = 1
AND tmt.create_date <=#{date2}
AND tmt.create_date >= #{date1}
tmtr.member_level=#{memberLevel}
GROUP BY date_format(tmtr.participate_date,'%Y-%m-%d')
