mapper.select:
SELECT count( * ) FROM ( SELECT COUNT( mkt_task_record_id ) c, member_code, task_id FROM `t_mkt_task_record` GROUP BY task_id, member_code HAVING
task_id = #{mktTaskId} ) a
a.c =#{shareTimes}

SELECT
SUM(consume_amount) AS totalConsume,
COUNT(mkt_task_record_id) AS totalTimes
FROM
t_mkt_task_record
WHERE
valid = 1
AND sys_brand_id =#{sysBrandId}
AND task_type =#{taskType}
AND member_code =#{memberCode}
AND task_id=#{taskIds}

SELECT tmtr.task_id AS taskId, tmt.task_code AS taskCode, tmt.task_name AS taskName,
SUM(tmtr.points) AS oneTaskPoints, count(distinct tmtr.member_code) AS oneTaskCompleteCountMbr FROM t_mkt_task_record tmtr INNER JOIN t_mkt_task tmt ON tmtr.task_id=tmt.mkt_task_id WHERE tmtr.valid = 1 AND tmt.valid=1 AND tmtr.rewarded = 1
AND tmtr.sys_brand_id = #{brandId} AND tmtr.task_type = #{taskType}
AND tmt.task_name like CONCAT('%',#{taskName},'%')
AND tmt.create_date <=#{date2}
AND tmt.create_date >= #{date1}
GROUP BY tmtr.task_id,tmtr.task_type
