mapper.select:
SELECT tmt.mkt_task_id AS mktTaskId, tmt.sys_company_id AS sysBrandId, tmt.sys_brand_id AS sysCompanyId, tmt.task_code AS taskCode, tmt.task_type AS taskType, tmt.task_name AS taskName, tmt.start_time AS startTime, tmt.end_time AS endTime, tmt.task_info AS taskInfo, tmt.points AS points, tmt.task_list_img AS taskListImg, tmt.task_detail_img AS taskDetailImg, tmt.check_status AS checkStatus, tmt.task_status AS taskStatus, tmt.remark AS remark, tmt.create_user_id AS createUserId, tmt.create_user_name AS createUserName, tmt.create_date AS createDate, tmt.modified_user_id AS modifiedUserId, tmt.modified_user_name AS modifiedUserName, tmt.modified_date AS modifiedDate, tmt.valid AS valid, tmts.share_times AS shareTimes, tmts.share_url_type AS shareUrlType, tmts.share_url AS shareUrl FROM `t_mkt_task` tmt INNER JOIN t_mkt_task_share tmts ON tmt.mkt_task_id = tmts.mkt_task_id AND tmt.valid = 1 AND tmts.valid = 1
AND tmt.task_status = #{taskStatus}
AND tmt.sys_brand_id = #{sysBrandId}
AND tmt.task_type = #{taskType}
